package code;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 1;
		switch(num){
			case 1:{
				System.out.println("The number is 1");
				break;
			}
			case 2:{
				System.out.println("The number is 2");
				break;
			}
			default:
				System.out.println("The number is not 1 or 2");
		}
	}

}
